mapper.select:
select a.yjrq, a.glbm,sum(wsjsc) wsjsc, sum(ztyc) ztyc, sum(zplyc) zplyc, sum(sjjz) sjjz, sum(sjjj) sjjj, sum(csyc) csyc,IFNULL(uf.zfscsb_count, 0) AS zfscsb from ( select yjrq, glbm,count(*) wsjsc,0 ztyc,0 zplyc, 0 sjjz, 0 sjjj, 0 csyc from dev_vio_equ_warning t where t.yjlx = '2'
AND yjrq >= #{params.beginTime}
AND yjrq <= #{params.endTime}
group by yjrq, glbm
union ALL
select yjrq, glbm,0 wsjsc,count(*) ztyc,0 zplyc, 0 sjjz, 0 sjjj, 0 csyc from dev_vio_equ_warning t where t.yjlx = '7'
AND yjrq >= #{params.beginTime}
AND yjrq <= #{params.endTime}
group by yjrq, glbm
union ALL
select yjrq, glbm,0 wsjsc,0 ztyc,count(*) zplyc, 0 sjjz, 0 sjjj, 0 csyc from ( select yjrq, glbm,0 wsjsc,0 ztyc,count(*) zplyc, 0 sjjz, 0 sjjj, 0 csyc from dev_vio_equ_warning t where t.yjlx = '1'
AND yjrq >= #{params.beginTime}
AND yjrq <= #{params.endTime}
group by yjrq, glbm, sbbh ) a group by a.yjrq, a.glbm
union all
select a.yjrq, a.glbm, 0 wsjsc, 0 ztyc, 0 zplyc, count(*) sjjz, 0 sjjj, 0 csyc from ( SELECT target_day.sbbh sbbh, target_day.glbm glbm, target_day.yjrq yjrq, target_day.total_shuliang shuliang, comparison_avg.avg_shuliang pjz, CASE WHEN comparison_avg.avg_shuliang IS NULL THEN NULL ELSE CONCAT(FORMAT((target_day.total_shuliang - comparison_avg.avg_shuliang) / NULLIF(comparison_avg.avg_shuliang, 0) * 100, 2), '%') END AS bz, CASE WHEN comparison_avg.avg_shuliang IS NULL THEN '无足够数据进行比较' WHEN comparison_avg.avg_shuliang = 0 THEN '基准平均值为0无法比较' WHEN (target_day.total_shuliang - comparison_avg.avg_shuliang) / comparison_avg.avg_shuliang >= 0.5 THEN '数据激增' WHEN (target_day.total_shuliang - comparison_avg.avg_shuliang) / comparison_avg.avg_shuliang <= -0.5 THEN '数据巨减' ELSE '无明显变化' END AS result FROM (
SELECT sbbh, glbm, yjrq, total_shuliang FROM ( SELECT sbbh, glbm, yjrq, SUM(shuliang) AS total_shuliang FROM dev_vio_equ_warning WHERE yjlx = 1 GROUP BY sbbh, glbm, yjrq ) AS daily_aggregated_data WHERE yjrq = #{yjrq} ) AS target_day LEFT JOIN (
SELECT sbbh, glbm, CASE WHEN valid_count < 3 THEN valid_sum / NULLIF(valid_count, 0) ELSE (valid_sum - valid_max - valid_min) / NULLIF(valid_count - 2, 0) END AS avg_shuliang FROM ( SELECT sbbh, glbm, COUNT(total_shuliang) AS valid_count, SUM(total_shuliang) AS valid_sum, MAX(total_shuliang) AS valid_max, MIN(total_shuliang) AS valid_min FROM ( SELECT sbbh, glbm, yjrq, SUM(shuliang) AS total_shuliang FROM dev_vio_equ_warning WHERE yjlx = 1 GROUP BY sbbh, glbm, yjrq ) AS t WHERE total_shuliang > 0 AND yjrq >= #{preDate} AND yjrq <= #{endDate} GROUP BY sbbh, glbm ) AS pre_calculation ) AS comparison_avg ON target_day.sbbh = comparison_avg.sbbh
) a where a.result like '%数据激增%' group by a.yjrq, a.glbm
union all
select a.yjrq, a.glbm, 0 wsjsc, 0 ztyc, 0 zplyc, 0 sjjz, count(*) sjjj, 0 csyc from ( SELECT target_day.sbbh sbbh, target_day.glbm glbm, target_day.yjrq yjrq, target_day.total_shuliang shuliang, comparison_avg.avg_shuliang pjz, CASE WHEN comparison_avg.avg_shuliang IS NULL THEN NULL ELSE CONCAT(FORMAT((target_day.total_shuliang - comparison_avg.avg_shuliang) / NULLIF(comparison_avg.avg_shuliang, 0) * 100, 2), '%') END AS bz, CASE WHEN comparison_avg.avg_shuliang IS NULL THEN '无足够数据进行比较' WHEN comparison_avg.avg_shuliang = 0 THEN '基准平均值为0无法比较' WHEN (target_day.total_shuliang - comparison_avg.avg_shuliang) / comparison_avg.avg_shuliang >= 0.5 THEN '数据激增' WHEN (target_day.total_shuliang - comparison_avg.avg_shuliang) / comparison_avg.avg_shuliang <= -0.5 THEN '数据巨减' ELSE '无明显变化' END AS result FROM (
SELECT sbbh, glbm, yjrq, total_shuliang FROM ( SELECT sbbh, glbm, yjrq, SUM(shuliang) AS total_shuliang FROM dev_vio_equ_warning WHERE yjlx = 1 GROUP BY sbbh, glbm, yjrq ) AS daily_aggregated_data WHERE yjrq = #{yjrq} ) AS target_day LEFT JOIN (
SELECT sbbh, glbm, CASE WHEN valid_count < 3 THEN valid_sum / NULLIF(valid_count, 0) ELSE (valid_sum - valid_max - valid_min) / NULLIF(valid_count - 2, 0) END AS avg_shuliang FROM ( SELECT sbbh, glbm, COUNT(total_shuliang) AS valid_count, SUM(total_shuliang) AS valid_sum, MAX(total_shuliang) AS valid_max, MIN(total_shuliang) AS valid_min FROM ( SELECT sbbh, glbm, yjrq, SUM(shuliang) AS total_shuliang FROM dev_vio_equ_warning WHERE yjlx = 1 GROUP BY sbbh, glbm, yjrq ) AS t WHERE total_shuliang > 0 AND yjrq >= #{preDate} AND yjrq <= #{endDate} GROUP BY sbbh, glbm ) AS pre_calculation ) AS comparison_avg ON target_day.sbbh = comparison_avg.sbbh
) a where a.result like '%数据巨减%' group by a.yjrq, a.glbm
union all
select b.yjrq, b.bmmc glbm,0 wsjsc,0 ztyc,0 zplyc, 0 sjjz, 0 sjjj, count(*) csyc from (select t.yjrq, t.bmbm, d.bmmc, t.sbbh, ifnull((sum(cszfl)/sum(zpl)),0) zfl from dev_in_use t left join fre_dep d on substr(t.bmbm,1,6) = d.bmbm where zpl >= 10
AND yjrq >= #{params.beginTime}
AND yjrq <= #{params.endTime}
group by t.sbbh ) b where b.zfl >= '0.2' group by b.bmbm
) a left join ( SELECT DATE_FORMAT(t.wfsj, '%Y-%m-%d') AS yjrq_failure, f.bmmc AS glbm_failure, COUNT(1) AS zfscsb_count FROM vio_suveil t LEFT JOIN dev_vio_equipment d ON d.sbbh = t.sbbh LEFT JOIN fre_dep f ON f.bmbm = SUBSTRING(d.sbbh, 1, 6) WHERE d.zt IN ('2', '3')
AND t.wfsj >= STR_TO_DATE(#{params.beginTime},'%Y-%m-%d')
AND t.wfsj <= STR_TO_DATE(#{params.endTime},'%Y-%m-%d')
GROUP BY DATE_FORMAT(t.wfsj, '%Y-%m-%d'), f.bmmc ) uf ON a.yjrq = uf.yjrq_failure AND a.glbm = uf.glbm_failure
and a.glbm = #{glbm} and glbm not like '%一大队%' and glbm not like '%二大队%' and glbm not like '%三大队%'
group by a.yjrq, a.glbm order by case when glbm like '%芝罘%' then 1 when glbm like '%莱山%' then 2 when glbm like '%高新%' then 3 when glbm like '%高速福山%' then 17 when glbm like '%高速海阳%' then 18 when glbm like '%高速栖霞%' then 19 when glbm like '%高速龙口%' then 20 when glbm like '%高速莱州%' then 21 when glbm like '%机场%' then 4 when glbm like '%海港%' then 5 when glbm like '%福山%' then 6 when glbm like '%开发%' then 7 when glbm like '%牟平%' then 8 when glbm like '%蓬莱%' then 9 when glbm like '%龙口%' then 10 when glbm like '%莱阳%' then 11 when glbm like '%莱州%' then 12 when glbm like '%招远%' then 13 when glbm like '%栖霞%' then 14 when glbm like '%海阳%' then 15 when glbm like '%长岛%' then 16 else 25 end

select t.yjrq, t.glbm, t.sbbh, d.sblx, d.sbmc from dev_vio_equ_warning t
left join dev_vio_equipment d on t.sbbh = d.sbbh
where t.yjlx = '2'
  and t.yjrq = #{yjrq}
  and t.glbm = #{glbm}

mapper.update:
update dev_vio_equ_warning
yjrq = #{yjrq}, glbm = #{glbm}, sbbh = #{sbbh}, yjllx = #{yjllx}, yjlx = #{yjlx}, shuliang = #{shuliang}, wfxw = #{wfxw}, cjsj = #{cjsj}, wsjsc = #{wsjsc}, ztyc = #{ztyc}, zplyc = #{zplyc}, sjjz = #{sjjz}, sjjj = #{sjjj}, csyc = #{csyc}, zfl = #{zfl}, bz = #{bz},
where id = #{id}
